INSERT INTO
    recipe(name, directions, time, ingredients, category_id, img, likes)
VALUES
    ('Pizza', 'W dużej miski wlej wode i dosyp suche drożdże, dokładnie wymieszaj. Dodajemy połowę mąki i
     mieszamy - ciasto powinno być jednolite. Stopniowo dosypujemy pozostałą mąkę i sól. Chwilę ugniatamy
     i odstawiamy na godzinę. Po tym czasie ugniatamy przez 5 minut i odstawiamy w temperaturze pokojowej
     na godzinę, a następnie do lodówki na 18-24 godziny. Na następny dzień ciasto wyjmujemy i dzielimy na 3 równe części,
     tworzymy kulki. Kulki układamy w pojemniku/miscę, przykrywamy i odstawiamy na kolejne 3-4 godziny w tem. pokojowej.
     Po tym czasie mamy gotowe ciasto. Nagrzewamy piekarnik z kamieniem na maksymalną temperaturę. Przygotuj sos i ulubione
     składniki. Pizzę pieczemy 3-5 minut. Smacznego!', '25h', '350g mąki typ 00, 10g soli, 560ml wody, 3g drożdzy suchych', 2,
     'https://www.mojegotowanie.pl/media/cache/default_view/uploads/media/recipe/0002/02/pizza-domowa.jpeg', 0),
    ('Pad thai', 'Na gorącym woku wrzuć kurczaka. Smaż przez 3 minuty i dodaj warzywa, po 2 minutach dodaj sos.Posyp szczypiorkiem. Smacznego',
     '30min', '300g kurczaka, 1 papryka, 1 cebula, 1 główka czosnku, sos sojowy', 3,
     'https://cdn.shopify.com/s/files/1/0559/7881/9741/files/Pad_Thai_-_prosty_przepis_Policzona_Szama_blog.jpg?v=1669244451', 0),
    ('Szakszuka', 'Zeszklij cebulę, dodaj czosnek. Po chwili pomidory i duś przez 5 minut. Po tym czasie zrób dziury w sosie
     i do każdej daj jajko. Przykryj minutę. Gotowe', '15min', 'puszka pomidorów, 2 jajka, sól, pieprz', 1,
     'https://szefsmaku.pl/wp-content/uploads/2022/05/szakszuka.jpg', 0),
    ('Banoffee', 'Do pucharków dodaj pokruszone ciasteczka. Następnie pokrojone banany i masę krówkową. Powtórz warstwy
     na koniec dodaj bitą śmietanę. Smacznego', '15min', '200g ciasteczek, 100ml masy krówkowej, 1 banan, 100ml bitej śmietany', 5,
     'http://migawkowo.pl/wp-content/uploads/2016/09/banoffee-deser-s%C5%82oik.jpg', 0),
    ('Frytki z batatów', 'Piekarnik nagrzać do 220 stopni C. Przygotować dużą blachę (np. z wyposażenia piekarnika) i wyłożyć ją papierem do pieczenia.
    Batata obrać i pokroić na cienkie słupki. Obtoczyć je w oliwie, następnie doprawić pieprzem (solimy dopiero na koniec, po upieczeniu), papryką, czosnkiem, oregano. Dodać skrobię ziemniaczaną i wymieszać.
    Bataty ułożyć na blaszce z zachowaniem odstępów. Posypać listkami rozmarynu (opcjonalnie). Wstawić do piekarnika i piec przez ok. 20 - 23 minuty.
    Po upieczeniu posypać posiekaną natką, a przed podaniem doprawić solą.','30min', '1 duży batat (ok. 400 g), 1/2 łyżki oliwy extra
    , po szczypcie pieprzu, papryki oraz czosnku granulowanego, 1/2 łyżeczki suszonego oregano, 1 łyżeczka skrobi ziemniaczanej,
    1 łyżka posiekanej natki pietruszki, sól morska', 4,
     'https://www.garneczki.pl/blog/wp-content/uploads/2018/09/przepis-na-frytki-z-batatow-1440x785.jpg', 0),
    ('Tosty francuskie', 'Jajka roztrzep widelcem, wymieszaj z napojem roślinnym albo mlekiem. Pieczywo namocz przez chwilę w masie jajecznej (chałka nasiąka bardzo szybko,
    inne rodzaje pieczywa mogą potrzebować chwili dłużej). Na patelni rozgrzej połowę oleju, dodaj połowę masła. Ułóż 4 kromki chałki. Smaż na wolnym ogniu na złoty kolor (po ok. 2,5 minuty z każdej strony).
    Przełóż na talerz, usmaż pozostałe kromki chałki na reszcie oleju i masła. Podawaj z jogurtem, owocami, cynamonem, syropem klonowym i orzechami.',
     '20min', 'Chałka 8 kromek, jajka M 3 szt., napój owsiany lub mleko 200 ml, olej 2 łyżki, masło 1 łyżka', 1,
    'https://praktykulinarni.com/wp-content/uploads/2022/04/tosty-francuskie-z-chalki-1024x682.jpg', 0),
    ('Omlet z cukinią i pomidorami', 'Cukinię pokrój na cienkie plasterki, pomidory na ćwiartki. Posiekaj dymkę i natkę pietruszki.
    Wrzuć wszystko na rozgrzaną oliwę. Przesmaż na dużym ogniu 1-2 minuty. Po czym zmniejsz ogień. Wylej jajka równomiernie na całą patelnię.
    Smaż wszystko na niedużym ogniu około 2-3 minut, aż omlet będzie prawie ścięty. Następnie posyp omlet startym serem i złóż na pół.
    Po kolejnych kilku minutach smażenia delikatnie zsuń omlet na talerz. Podawaj natychmiast z sałatą i pieczywem.', '15min',
    '3 jajka, mała cukinia, pomidory koktajlowe 4 sztuki, 3 szczypty posiekanej pietruszki, 1 łyżka oliwy z oliwek',
     1, 'https://s3.przepisy.pl/przepisy3ii/img/variants/800x0/omlet-z-cukinia-pomidorami-i-owczym-serem.jpg', 0),
    ('Tortilla z kurczakiem', 'Filet z kurczaka pokroić na podłużne kawałki. Doprawić solą, pieprzem, papryką słodką i ostrą oraz oregano.
    Delikatnie oprószyć mąką, następnie wymaczać w roztrzepanym jajku a na koniec w bułce tartej lub panko.
    Usmażyć na złoty kolor na patelni z olejem. Odłożyć na talerz wyłożony ręcznikiem papierowym. Tortille podgrzać, na środek nałożyć poszatkowaną sałatę, następnie położyć po 3 kawałki kurczaka, pokrojonego pomidora, ogórka i paprykę.
    Posypać tartym serem, dodać cebulę pokrojoną w piórka oraz rukolę. Dodać 2 - 3 łyżeczki sosu (pomieszany w równych proporcjach majonez z ketchupem lub sosem pomidorowym, ewent. z odrobiną srirachy).
    Posypać szczypiorkiem i zawinąć tortillę: najpierw zawinąć dół a potem do środka dwa przeciwległe boki.', '30min',
    '3 placków tortilla, 1 czerwona papryka, 2 pomidory, 2 ogórki gruntowe lub 1 szklarniowy, 3 - 4 liście kapusty pekińskiej, 1 czerwona cebula, podwójna pierś z kurczaka, 2 łyżki przyprawy do gyrosa, olej do smażenia, 3- 4 łyżki jogurtu naturalnego, 3 łyżki majonezu, 2 łyżki musztardy, czarny pieprz, sól',
     2, 'https://staticsmaker.iplsc.com/smaker_prod_2021_03_26/4f8c40ea3ddb3a712b49766a94504ce4-recipe_main.jpg', 0),
    ('Salatka z burakiem i serem kozim', 'Obrane buraczki pokroić w kostkę. Ułożyć na opłukanej i osuszonej roszponce lub mieszance sałat, razem z plasterkami sera koziego.
    Orzechy włoskie delikatnie podprażyć na suchej patelni. Zdjąć z ognia, wymieszać z 1 łyżeczką oliwy. Dodać do sałatki.
    Składniki sosu umieścić w słoiczku, zakręcić i kilka razy energicznie potrząsnąć mieszając składniki. Polać po sałatce.',
     '10min', '2 pieczone lub gotowane buraczki, 80 g roszponki lub mix sałat z botwinką, 80 g sera koziego (roladka), 30 g orzechów włoskich
    , 1 łyżeczka oliwy extra, sos miodowo-musztardowy', 2, 'https://staticsmaker.iplsc.com/smaker_prod_2019_06_13/56c60548c29edd1d249069441ac7ac83-recipe_main.jpg', 0),
    ('Ryba z pieca z brokułami', 'Filety opłukać i dokładnie osuszyć papierowymi ręcznikami. Doprawić solą, pieprzem, czosnkiem, kurkumą i papryką. Na koniec wysmarować oliwą i obtoczyć w mące.
    Ułożyć na blaszce do pieczenia z zachowaniem odstępów. Piekarnik nagrzać do 180 stopni C.
    Odciać różyczki brokuła i pokroić je na drobniejsze kawałeczki. Wrzucić na wrzątek i po ponownym zagotowaniu się wody gotować przez ok. 2 minuty, odcedzić na sitku oraz doprawić solą.
    Sos: na patelnię wsypać mąkę i podgrzać ją. Mieszając drewnianą łyżką smażyć ok. 1 - 2 minuty. Dodać masło, roztopić i mieszając zagotować.
    Stopniowo wlewać bulion, a potem śmietankę, cały czas energicznie mieszając rózgą aż sos będzie gładki. Pod koniec doprawić solą oraz gałką muszkatołową i wymieszać z parmezanem.
    Na rybę wyłożyć brokuły a następnie sos. Posypać płatkami migdałów i wstawić do nagrzanego piekarnika. Piec przez ok. 30 minut.',
    '40min','500 g filetów białej ryby (np. dorsz, morszczuk, mintaj), po 1/3 łyżeczki soli, pieprzu, czosnku, kurkumy, papryki, 1 łyżka oliwy
    , 1 łyżka mąki pszennej, 1/2 brokuła, 50 g płatków migdaów', 3, 'https://e-przepisykulinarne.pl/wp-content/uploads/2011/10/zapiekanka_ryba1.jpg', 0),
    ('Canneloni ze szpinakiem', 'Szpinak rozmrozić (np. w mikrofalówce), odcisnąć z nadmiaru wody.
    Nastawić sos pomidorowy: do garnka wlać oliwę, dodać pokrojoną w kosteczkę cebulę i zeszklić ją. Dodać starty czosnek i chwilę razem podsmażać.
    Wlać przecier pomidorowy oraz bulion, doprawić solą, pieprzem, oregano i chili, zagotować. Gotować pod uchyloną pokrywą, na małym ogniu, przez ok. 10 - 15 minut.
    Piekarnik nagrzać do 180 stopni C. Do szpinaku dodać ricottę, tarty twardy ser, przeciśnięty przez praskę czosnek, jajko oraz gałkę muszkatołową. Dokładnie wymieszać składniki, doprawić solą i pieprzem.
    Rurki makaronowe nadziać przygotowanym farszem szpinakowym i układać w formie żaroodpornej wysmarowanej oliwą.
    Polać sosem pomidorowym i wstawić do nagrzanego piekarnika. Piec bez przykrycia przez 25 minut.
    Następnie położyć na wierzchu plastrerki lub tartą mozzarellę i piec jeszcze przez 10 minut.', '60min', '150 g makaronu cannelloni
    , 450 g mrożonego siekanego szpinaku, 250 g ricotty , 80 g tartego twardego sera (jak Dziugas, grana padano, parmezan)
    , 1 ząbek czosnku, 1 jajko, 1/3 łyżeczki tartej gałki muszkatołowej, 140 g żółtej mozzarelli (np. plastry, tarta), 1 łyżka oliwy extra, sos pomidorowy',
     3, 'https://imbirowygarnek.pl/wp-content/uploads/2017/06/Cannelloni-ze-szpinakiem-i-ricotta.jpg', 0),
    ('Pieczona owsianka brownie', 'Piekarnik nagrzać do 180 stopni C. Płatki owsiane wsypać do foremek żaroodpornych (tak aby sięgały nie więcej niż do ich połowy) - można użyć kilku kokilek lub jednej formy.
    Mleko podgrzać prawie do zagotowania z dodatkiem kakao, cukru, syropu lub miodu, cynamonu i masła orzechowego. W międzyczasie wymieszać składniki.
    Mlekiem zalać płatki w foremkach, zostawiając ok. 1 - 2 cm na dodatki. Do naczynek dodać drobniej posiekaną czekoladę a następnie maliny. Wstawić do piekarnika na ok. 20 - 25 minut.',
     '30min', '1 szklanka płatków owsianych (100 g), 1 i 3/4 szklanki mleka (zwykłe lub roślinne), 2 łyżki gorzkiego (naturalnego) kakao
    , 2 łyżki cukru trzcinowego, 1 łyżka syropu klonowego lub miodu, szczypta cynamonu, 2 łyżki masła orzechowego
    , od 50 - 100 g czekolady gorzkiej lub lekko gorzkiej, szklanka świeżych malin lub 1/2 szklanki mrożonych', 4,
     'https://dietetykpowszechny.pl/wp-content/uploads/2021/11/IMG_5791-scaled-1920x960.jpg', 0),
    ('Creme brulee', 'W rondelku na małym ogniu zagotować śmietankę z dodatkiem cukru i wyłuskanych łyżeczką ziarenek z przeciętej wzdłuż laski wanilii. Gdy śmietanka zacznie się gotować na brzegach, odstawić z ognia i przestudzić.
    Żółtka ubijać mikserem przez około pół minuty (nie na puszystą masę). Dolewać ciepłą śmietanę, po łyżce lub cienkim strumieniem, cały czas miksując na małych obrotach miksera bez ubijania kremu. Ma on być gładki i błyszczący.
    Piekarnik nagrzać do 100 stopni C. Foremki do zapiekania napełnić masą wlewając ją przez sitko. Foremki postawić na blaszce do pieczenia.
    Wstawić desery do piekarnika i piec przez 50 minut. Krem powinien być ścięty. Wyjąć, ostudzić i schłodzić w lodówce, najlepiej przez całą noc.
    Po schłodzeniu wierzch deseru posypać równą warstwą cukru i opalić go specjalnym palnikiem do creme brulee. Cukier się rozpuści, zrumieni i utworzy się charakterystyczna skorupka.
    Udekorować świeżymi owocami i miętą.', '2h', '500 ml śmietanki kremówki 36%, 6 żółtek, 1/3 szklanki cukru
    , 1 laska wanilii', 5, 'https://static.fajnegotowanie.pl/media/uploads/media_image/auto/recipe-content/1237/desktop/creme-brulee.jpg.webp', 0),
    ('Smoothie szpinakowe', 'Szpinak opłukać, włożyć do pojemnika stojącego blendera, dodać obranego i pokrojonego banana,
    obrany i pokrojony na plasterki imbir oraz wlać sok jabłkowy. Zmiksować dokładnie do uzyskania gładkiej konsystencji.',
     '5min', 'garść szpinaku (50 g), 1/2 banana, 1 cm korzenia imbiru (8 g), 1 szklanka (250 ml) soku jabłkowego',
     4, 'http://www.clatronic.pl/img/cms/blog/proficook/5/Smoothie-ze-szpinakiem-kiwi.jpg', 0),
    ('Tiramisu', 'Jajka włożyć do zlewu i przelać gorącą wodą. Zaparzyć kawę, dodać likier i całość ostudzić. Oddzielić żółtka od białek.
    Wszystkie 4 żółtka ubić z cukrem pudrem na puszysty i jasny krem (ok. 7 minut) - najlepiej początkowo ubijać na parze (w metalowej misce zawieszonej na garnku z parującą wodą), a gdy żółtka będą już ciepłe, odstawić z pary i dalej ubijać.
    Następnie dodawać porcjami (po 3 łyżki) mascarpone, ale już w krótszych odstępach czasu, cały czas ubijając, aż krem będzie gęsty i jednolity.
    W oddzielnej misce ubić białka na idealnie sztywną pianę z dodatkiem małej szczypty soli. Połączyć je z kremem z żółtek i mascarpone delikatnie mieszając łyżką.
    Połowę biszkoptów na moment zanurzać w kawie i układać w prostokątnym naczyniu np. żaroodpornym lub szklanym (ok. 20 x 22 cm) lub w pojedynczych naczynkach. Posypać cienką warstwą kakao.
    Wyłożyć połowę kremu i przykryć kolejną warstwą nasączonych biszkoptów. Znów oprószyć kakao. Posmarować resztą kremu, posypać kakao i wstawić do lodówki na minimum 3 godziny lub na całą noc.',
     '1h', '350 ml zaparzonej mocniejszej kawy, 25 ml likieru amaretto lub Maraschino, 2 jajka + 2 żółtka
    , 70 g cukru pudru, 500 g serka mascarpone, 300 g podłużnych biszkoptów, ok. 3 łyżek gorzkiego kakao', 5,
     'https://polki.pl/foto/4_3_LARGE/tiramisu-2350187.webp', 0);

INSERT INTO
    category(name, description, img)
VALUES
    ('Śniadanie', 'Śniadanie to pierwszy i najważniejszy posiłek dnia, który powinien dostarczyć najwięcej energii.
    Tradycyjne polskie śniadanie wielu osobom kojarzy się z chrupiącym i świeżo wypieczonym chlebem
    czy bułkami oraz dodatkami takimi jak jajka, ser, szynka itp.',
    'https://szefsmaku.pl/wp-content/uploads/2019/02/sniadanie-to-podstawa.jpg'),
    ('Obiad', 'Obiad to dla wielu najbardziej wyczekiwany posiłek w ciągu dnia. Bez niego nie wyobrażamy sobie pełnowartościowej diety,
    która powinna być bogata w witaminy i minerały. A ich nigdy nie brakuje w potrawach, które są podstawą dziennego menu.',
     'https://bi.im-g.pl/im/d8/81/1b/z28843992IH,Tani-i-szybki-obiad--Oto-przepis-na-danie--za-ktor.jpg'),
    ('Kolacja', 'Kolacja – ostatni posiłek wieczorny. Słowo kolacja wywodzi się z języka starołacińskiego od collatio, collationis w znaczeniu zbiórki,
    składki pieniężnej, ale także zebrania, zgromadzenia dostojnych gości na niezwykle uroczystej biesiadzie, uczcie czy wieczerzy.',
     'https://staticsmaker.iplsc.com/smaker_prod_2021_10_06/2cc9f03b97a3019b6f5efaa28552ec0d-recipe_main.jpg'),
    ('Przekąska', 'Szybkie przekąski są niezastąpione podczas imprez, takich jak urodziny, imieniny, sylwester czy przyjęcia świąteczne.
    Sprawdzają się także jako małe co nieco do podjadania w ciągu dnia.',
     'https://szefsmaku.pl/wp-content/uploads/2019/12/przekaski-1250x712.jpg'),
    ('Deser', 'Leniwe popołudnie to jedna z ulubionych pór dnia łasuchów. To właśnie wtedy przychodzi czas
    na długo wyczekiwany po obiedzie deser, który może mieć przecież tak wiele postaci!',
     'https://polki.pl/foto/4_3_LARGE/idealny-tort-bezowy-zobacz-prosty-przepis-na-ten-pyszny-deser-2447398.webp');

